// Parse AI response
const aiResponse = JSON.parse($json.response.text);
const strategies = aiResponse.strategies;
const historicalData = $node["Fetch Historical Stock Data"].json;

// Backtesting function
function backtestStrategy(strategy, data) {
  const results = {
    strategy_name: strategy.name,
    tickers: strategy.tickers,
    total_trades: 0,
    winning_trades: 0,
    losing_trades: 0,
    total_return_pct: 0,
    max_drawdown_pct: 0,
    sharpe_ratio: 0,
    trades: []
  };

  // Simple moving average crossover backtest example
  // This is a simplified version - you would implement more complex logic based on strategy
  for (const ticker of strategy.tickers) {
    if (!data[ticker]) continue;

    const prices = data[ticker];
    let position = null;
    let capital = 10000; // Starting capital per ticker
    let initialCapital = capital;
    let peak = capital;

    for (let i = 20; i < prices.length; i++) {
      const currentPrice = prices[i].Close;
      const sma20 = prices.slice(i-20, i).reduce((sum, p) => sum + p.Close, 0) / 20;
      const sma5 = prices.slice(i-5, i).reduce((sum, p) => sum + p.Close, 0) / 5;

      // Entry: Short MA crosses above Long MA (simplified entry condition)
      if (!position && sma5 > sma20) {
        const shares = Math.floor((capital * strategy.position_size_pct / 100) / currentPrice);
        position = {
          ticker: ticker,
          entry_price: currentPrice,
          entry_date: prices[i].Date,
          shares: shares,
          stop_loss: currentPrice * (1 - strategy.stop_loss_pct / 100)
        };
      }

      // Exit conditions
      if (position) {
        const currentValue = position.shares * currentPrice;
        const entryValue = position.shares * position.entry_price;
        const returnPct = ((currentValue - entryValue) / entryValue) * 100;

        // Exit on stop loss, take profit, or MA crossover
        if (currentPrice <= position.stop_loss || sma5 < sma20 || i - prices.findIndex(p => p.Date === position.entry_date) >= strategy.holding_period_days) {
          const profit = currentValue - entryValue;
          capital += profit;

          results.total_trades++;
          if (profit > 0) results.winning_trades++;
          else results.losing_trades++;

          results.trades.push({
            ticker: ticker,
            entry: position.entry_price,
            exit: currentPrice,
            return_pct: returnPct,
            profit: profit
          });

          position = null;
        }

        // Track drawdown
        if (capital > peak) peak = capital;
        const drawdown = ((peak - capital) / peak) * 100;
        if (drawdown > results.max_drawdown_pct) {
          results.max_drawdown_pct = drawdown;
        }
      }
    }

    // Calculate total return
    results.total_return_pct = ((capital - initialCapital) / initialCapital) * 100;
  }

  // Calculate win rate
  results.win_rate_pct = results.total_trades > 0 
    ? (results.winning_trades / results.total_trades) * 100 
    : 0;

  // Simplified Sharpe ratio (would need more data for accurate calculation)
  const avgReturn = results.total_return_pct / results.tickers.length;
  results.sharpe_ratio = results.max_drawdown_pct > 0 
    ? avgReturn / results.max_drawdown_pct 
    : 0;

  return results;
}

// Backtest all strategies
const backtestResults = strategies.map(strategy => backtestStrategy(strategy, historicalData));

// Return results with strategy details and AI analysis
return [{
  timestamp: new Date().toISOString(),
  strategies: strategies,
  backtest_results: backtestResults,
  market_analysis: aiResponse.market_analysis,
  learning_notes: aiResponse.learning_notes,
  best_strategy: backtestResults.reduce((best, current) => 
    current.sharpe_ratio > (best?.sharpe_ratio || -Infinity) ? current : best
  , null)
}];

